mapper.select:
   		/*loginDAO idChkLogin 2020.12.18 ORW*/
		SELECT COUNT(*) FROM HL_USER
		WHERE 1=1 
		AND USER_ID = #{userId}
		AND USER_PW = #{userPw}
		AND AVAILABLE_CHK = 'Y'
		AND USE_YN = 'Y'

mapper.insert:
   		/*loginDAO loginLog 2020.12.18 ORW*/
		INSERT INTO HL_USER_LOG (USER_ID, USER_LOC, USER_FROM, LOGIN_DT)
		VALUES
		(#{userId}, #{userLoc}, #{userFrom}, now());

mapper.select:
   		/*loginDAO idChk 2020.12.18 ORW*/
		SELECT COUNT(*) FROM HL_USER
		WHERE 1=1 
		AND USER_ID = #{userId}

mapper.insert:
   		/*loginDAO signUp 2020.12.18 ORW*/
		INSERT INTO HL_USER (USER_ID, USER_PW, USER_NM, USER_EMAIL, USER_CHK, USER_BIRTH, AVAILABLE_CHK, USE_YN, UPDATE_DT)
		values
		(#{userId}, #{userPw}, #{userNm}, #{userEmail}, '1', STR_TO_DATE(#{userBirth}, '%Y%m%d'), 'Y', 'Y', now());
